mapper.select:
SELECT 
FROM ability_goals g
WHERE g.id = #{id}

mapper.update:
UPDATE ability_goals
student_id = #{studentId}, dimension_id = #{dimensionId}, title = #{title}, description = #{description}, target_score = #{targetScore}, current_score = #{currentScore}, target_date = #{targetDate}, priority = #{priority}, status = #{status}, achieved_at = #{achievedAt}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT 
FROM ability_goals g
WHERE g.student_id = #{studentId}
AND g.dimension_id = #{dimensionId}
AND g.status = 'active'
ORDER BY g.target_date

SELECT 
FROM ability_goals g

    AND g.student_id = #{studentId}
    AND g.dimension_id = #{dimensionId}
    AND g.status = #{status}

ORDER BY g.created_at DESC
LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM ability_goals g

    AND g.student_id = #{studentId}
    AND g.dimension_id = #{dimensionId}
    AND g.status = #{status}
