mapper.select:
SELECT 
FROM ai_messages
WHERE conversation_id = #{conversationId}
ORDER BY created_at ASC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM ai_messages
WHERE conversation_id = #{conversationId}

mapper.delete:
DELETE FROM ai_messages
WHERE conversation_id = #{conversationId}
  AND id IN (
      SELECT id FROM (
          SELECT id
          FROM ai_messages
          WHERE conversation_id = #{conversationId}
          ORDER BY created_at ASC
          LIMIT GREATEST(0, (SELECT COUNT(*) FROM ai_messages WHERE conversation_id = #{conversationId}) - #{keep})
      ) AS t
  )
