mapper.select:
SELECT COUNT(1) FROM enrollments e
WHERE 1=1

    AND e.student_id = #{studentId}


    AND e.course_id = #{courseId}


    AND e.status = #{status}

SELECT e.*, u.display_name as student_name, c.title as course_name, t.display_name as teacher_name FROM enrollments e LEFT JOIN users u ON e.student_id = u.id LEFT JOIN courses c ON e.course_id = c.id LEFT JOIN users t ON c.teacher_id = t.id WHERE 1=1
AND e.student_id = #{studentId}
AND e.course_id = #{courseId}
AND e.status = #{status}
AND (u.display_name LIKE CONCAT('%', #{keyword}, '%') OR c.title LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY e.enrolled_at DESC
