mapper.insert:
INSERT INTO grades (
    student_id, assignment_id, submission_id, grader_id,
    score, max_score, percentage, grade_level,
    feedback, status, created_at, updated_at, deleted
) VALUES (
             #{studentId}, #{assignmentId}, #{submissionId}, #{graderId},
             #{score}, #{maxScore}, #{percentage}, #{gradeLevel},
             #{feedback}, #{status}, #{createdAt}, #{updatedAt}, #{deleted}
         )

mapper.select:
SELECT 
FROM grades
WHERE student_id = #{studentId}
AND assignment_id = #{assignmentId}
AND deleted = false

SELECT g.
FROM grades g
INNER JOIN assignments a ON g.assignment_id = a.id
WHERE g.student_id = #{studentId}
AND a.course_id = #{courseId}
AND g.deleted = false

SELECT 
FROM grades
WHERE student_id = #{studentId}
AND deleted = false
ORDER BY created_at DESC
LIMIT #{size} OFFSET #{offset}

SELECT 
FROM grades
WHERE assignment_id = #{assignmentId}
AND deleted = false
ORDER BY score DESC
LIMIT #{size} OFFSET #{offset}

SELECT u.username as studentName, u.email as studentEmail, c.title as courseTitle, a.title as assignmentTitle, g.score, g.max_score as maxScore, g.percentage, g.grade_level, g.feedback, g.status, g.created_at as createdAt, g.updated_at as updatedAt FROM grades g JOIN users u ON g.student_id = u.id JOIN assignments a ON g.assignment_id = a.id JOIN courses c ON a.course_id = c.id WHERE g.deleted = false
AND a.course_id = #{courseId}
AND g.assignment_id = #{assignmentId}
ORDER BY c.title, a.title, u.username

SELECT a.title as assignmentTitle, g.score, g.percentage, g.grade_level, g.created_at as gradedAt FROM grades g JOIN assignments a ON g.assignment_id = a.id WHERE g.student_id = #{studentId} AND g.deleted = false AND g.status = 'published'
AND a.course_id = #{courseId}
AND g.created_at BETWEEN #{startDate} AND #{endDate}
ORDER BY g.created_at
